var siCss=document.createElement('link');siCss.setAttribute('rel','stylesheet');siCss.setAttribute('type','text/css');siCss.setAttribute('href','http://informers.sinoptik.ua/css/informers.css?v=3');document.getElementsByTagName('head')[0].appendChild(siCss);function siClickCount(){var siCount=document.createElement('script');siCount.setAttribute('type','text/javascript');siCount.setAttribute('src','http://informers.sinoptik.ua/counter.php?lang=ua&rnd='+new Date().valueOf());document.getElementsByTagName('head')[0].appendChild(siCount);}function LoadInformer(data){if(document.getElementById('siHeader')){document.getElementById('siHeader').innerHTML=data.date};for(var i=0;i< data.body.length;i++){if(data.body[i]!=null){if(document.getElementById('siT'+i)){document.getElementById('siT'+i).innerHTML=data.body[i].temp};if(document.getElementById('weatherIco'+i)){document.getElementById('weatherIco'+i).className='weatherIco '+data.body[i].img};if(document.getElementById('vl'+i)){document.getElementById('vl'+i).innerHTML=data.body[i].rh};if(document.getElementById('dav'+i)){document.getElementById('dav'+i).innerHTML=data.body[i].p};if(document.getElementById('wind'+i)){document.getElementById('wind'+i).innerHTML=data.body[i].ws};}}document.getElementById('SinoptikInformer').style.display='block';}LoadInformer( {date:'03.04.22, ранок', body:[{"temp":"+9&deg;","wt":null,"ws":"2 \u043c\/\u0441, <span class=\"wind wind-W\" title=\"\u0437\u0430\u0445\u0456\u0434\u043d\u0438\u0439\"><\/span>","p":"736 \u043c\u043c","rh":"93%","img":"d410"}]} )